// src/components/ActivityFeed/ActivityFeed.jsx
import { useEffect, useState } from 'react';
import { socket } from '../../utils/socket';
import { Clock, MessageSquare, UserPlus } from 'react-icons/feather';

export default function ActivityFeed() {
  const [activities, setActivities] = useState([]);

  useEffect(() => {
    socket.on('activity-update', (activity) => {
      setActivities(prev => [activity, ...prev.slice(0, 9)]);
    });

    return () => socket.off('activity-update');
  }, []);

  const getIcon = (type) => {
    switch(type) {
      case 'comment': return <MessageSquare />;
      case 'task': return <Clock />;
      case 'member': return <UserPlus />;
      default: return <Clock />;
    }
  };

  return (
    <div className="activity-feed">
      <h3>Recent Activity</h3>
      {activities.map((activity, index) => (
        <div key={index} className="activity-item">
          <div className="activity-icon">
            {getIcon(activity.type)}
          </div>
          <div className="activity-content">
            <strong>{activity.user}</strong> {activity.text}
            <small>{new Date(activity.timestamp).toLocaleTimeString()}</small>
          </div>
        </div>
      ))}
    </div>
  );
}
