import React, { useState } from "react";
import { Routes, Route } from "react-router-dom";
import { SocketProvider } from "./utils/socketContext";
import Navbar from "./components/Navbar/Navbar";
import Home from "./pages/Home/Home";
import Profile from "./pages/Profile/Profile";
import Messages from "./pages/Messages/Messages";
import { unstable_HistoryRouter as HistoryRouter } from "react-router-dom";
import { createBrowserHistory } from "history";
import Dashboard from "./pages/Dashboard/Dashboard";
import ProjectPage from "./pages/ProjectPage/ProjectPage";
import ActivityFeed from "./components/ActivityFeed/ActivityFeed";
import "./App.css";

// âœ… Fix: Correct history setup with future flags
const history = createBrowserHistory({
  future: {
    v7_startTransition: true, // Enables React.startTransition()
    v7_relativeSplatPath: true, // Fixes relative splat paths
  },
});

function App() {
  const [darkMode, setDarkMode] = useState(false);

  return (
    <SocketProvider>
      <div className={`app-container ${darkMode ? "dark-mode" : "light-mode"}`}>
        {/* âœ… Fix: Use HistoryRouter instead of BrowserRouter */}
        <HistoryRouter history={history}>
          <Navbar />
          <button onClick={() => setDarkMode(!darkMode)} className="theme-toggle">
            {darkMode ? "ðŸŒž Light" : "ðŸŒ™ Dark"}
          </button>

          <main className="main-content">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/project/:id" element={<ProjectPage />} />
              <Route path="/profile" element={<Profile />} />
              <Route path="/messages" element={<Messages />} />
            </Routes>
          </main>

          <ActivityFeed />
        </HistoryRouter>
      </div>
    </SocketProvider>
  );
}

export default App;
